mapper.select:
SELECT CALENDAR_SEQ 
	FROM ${hellonoteId}_${menuId}
	WHERE TITLE=#{title} AND START_DATE= to_date(#{startDate},'yyyy-mm-dd hh24 mi')

mapper.update:

	UPDATE ${hellonoteId}_${menuId}
	SET TITLE= #{title},
	    START_DATE= to_date(#{startDate},'yyyy-mm-dd hh24 mi'),
	    END_DATE= to_date(#{endDate},'yyyy-mm-dd hh24 mi'),
	    TYPE= #{type},
	    BACKGROUND_COLOR= #{backgroundColor},
	    ALL_DAY= #{allDay},
	    DESCRIPTION= #{description}
	WHERE CALENDAR_SEQ = #{calendarSeq}

	UPDATE ${hellonoteId}_${menuId}
	   SET START_DATE= to_date(#{startDate},'yyyy-mm-dd hh24 mi'),
	   	   END_DATE= to_date(#{endDate},'yyyy-mm-dd hh24 mi')
	   WHERE TITLE=#{title}
