mapper.delete:
delete from
${hellonoteId}_memo
where memo_seq=#{memoSeq}
and menu_id=#{menuId}

mapper.update:
		update kang_memo
		set
		memo_order = (select memo_order from kang_memo where
		memo_seq=#{memoSeq})
		where memo_order=#{memoOrder}

		update kang_memo
		set
		memo_order=#{memoOrder}
		where memo_seq=#{memoSeq}

update ${hellonoteId}_memo

	set memo_order = memo_order - 1
	where memo_order between
	#{oldOrder} and #{memoOrder}


	set memo_order = memo_order + 1
	where memo_order between
	#{memoOrder} and #{oldOrder}

and menu_id = #{menuId}
and memo_order != #{oldOrder}

update
${hellonoteId}_memo
set memo_order = #{memoOrder}
where memo_seq =
#{memoSeq}
and menu_id = #{menuId}
