mapper.select:
SELECT ACCOUNTBOOK_SEQ "accountbookSeq", ACCOUNTBOOK_DATE "accountbookDate", ACCOUNTBOOK_TRANSLATION "accountbookTranslation", ACCOUNTBOOK_PRICE "accountbookPrice", ACCOUNTBOOK_PURPOSE "accountbookPurpose" FROM KANG_ACCOUNTBOOK
ACCOUNTBOOK_DATE BETWEEN #{accountbookFirstDate} and #{accountbookLastDate}
AND ACCOUNTBOOK_PURPOSE=#{accountbookPurpose}
ORDER BY ACCOUNTBOOK_SEQ ASC
